print("Задание 1")
def linear_search(arr, n, key):
    for i in range(0, n):
        if arr[i] == key:
            return i
    return -1
arr = [1, 8, 123, 783, 385]
n = len(arr)
key = 123
print(linear_search(arr, n, key))
